<?php

namespace DevZeros\Mailer\Builders;

class BuilderMail
{
    private $template = "";
    private $text = "";
    private $countAction = 0;
    private $replace;

    public function __construct($file = ABSPATH . "View/template.php")
    {
        $this->template = file_get_contents($file);
    }

    public function title($text = "Ejemplo", $replace = ":title-page")
    {
        $this->template = str_replace(
            $replace,
            $text,
            $this->template
        );

        return $this;
    }

    public function greeting($text = "es simply dummy text of the printing and typesetting", $replace = ":greeting-page")
    {
        $this->template = str_replace(
            $replace,
            $text,
            $this->template
        );

        return $this;
    }

    public function logo($img = "data:image/png;base64,", $replace = ":logo-page")
    {
        $this->template = str_replace(
            $replace,
            $img,
            $this->template
        );

        return $this;
    }

    public function line($text = "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum", $replace = ":line-page")
    {
        $this->replace = $replace;
        $this->text .= "<br><span>" . $text . "</span><br>";

        return $this;
    }

    private function builderLine()
    {
        $this->template = str_replace(
            $this->replace,
            $this->text,
            $this->template
        );
    }

    public function firm($position = "Dev", $firm = "Carlos Andrés Castilla García", $tel = "", $email = "")
    {
        $this->template = str_replace(
            ':firm-page',
            $position . ": " . $firm,
            $this->template
        );

        if (!empty($tel))
        {
            $this->template = str_replace(
                '<p id="tel" style="display: none;',
                '<p id="tel" style="display: block;',
                $this->template
            );

            $this->template = str_replace(
                ':tel-page',
                $tel,
                $this->template
            );
        }
        if (!empty($email))
        {
            $this->template = str_replace(
                '<p id="email" style="display: none;',
                '<p id="email" style="display: block;',
                $this->template
            );

            $this->template = str_replace(
                ':email-page',
                $email,
                $this->template
            );
        }

        return $this;
    }

    public function action($text = "action", $url = "link.com")
    {
        if ($this->countAction > 0)
        {
            return $this;
        }

        $this->template = str_replace(
            '<td style="padding: 20px 0 0 0; display: none">',
            '<td style="padding: 20px 0 0 0; display: block">',
            $this->template
        );

        $this->template = str_replace(
            ':action-page',
            $text,
            $this->template
        );
        $this->template = str_replace(
            ':action-send-page',
            $url,
            $this->template
        );
        $this->countAction++;

        return $this;
    }

    public function footer($text = "©2019 Dev-Zeros. All rights reserved.", $name = "zeros", $link = "zeros.com")
    {
        $this->template = str_replace(
            ':footer-page',
            "{$text} <a href='{$link}' target='_blank' style='color: blue'>{$name}</a>",
            $this->template
        );

        return $this;
    }

    public function footerImg($img = "data:image/png;base64,", $replace = ":logo-footer-page")
    {
        $this->template = str_replace(
            $replace,
            $img,
            $this->template
        );

        return $this;
    }

    public function build()
    {
        $this->builderLine();

        return $this->template;
    }
}